import moment from 'moment'; // Example for onSort prop
import React, { Component } from 'react'; // Import React
import axios from 'axios';
import {Link, withRouter} from 'react-router-dom';
// import {bindActionCreators} from 'redux';
// import action from '../src/action/action';
import { render } from 'react-dom'; // Import render method
import Datatable from 'react-bs-datatable'; // Import this package
// import { connect } from 'react-redux';
import swal from 'sweetalert';

class DataTable extends Component{ 
    constructor(props){
        super(props);
        this.state = {
          vendorList : [],
          type 	 : 'Vendor',
        }
        this.vendorFetch = this.vendorFetch.bind(this);
      }

        vendorFetch(){
          let obj = {};
                obj.type = this.state.type;
          axios.post('http://localhost:3300/api/vendorList',obj).then((response)=>{
            console.log('BBBBBBBBBBBBBBBBBB',response);
            this.setState({
              vendorList: response.data.doc
            })
          })
        }

        componentWillMount(){
          this.vendorFetch();
        }


render(){
const header = [
  { title: 'Vendor Name', prop: 'category', sortable: true, filterable: true },
  { title: 'Store Name', prop: 'name', sortable: true ,filterable: true},
  // { title: 'Name Uppercased', prop: 'realnameuppercase', cell: (row) => row.realname.toUpperCase() },
  { title: 'Email Id', prop: 'price',      sortable: true,filterable: true },
  { title: 'Mobile No', prop: 'quantity',sortable: true,filterable: true },
  { title: 'Discount', prop: 'discount',sortable: true,filterable: true },
  { title: 'Action', prop: 'action'    ,sortable: true , filterable: true },
  ];

let state = this.state;
const body = [];
  state.vendorList.map((e,i)=>{
    body.push({'category' : e.category,
     'name':e.productName,
     'price': e.productPrice,
      'quantity': e.quantity,
      'discount': e.discount,
      'action': <div className="actiontrans" > 
       <Link to = "#"  > <i className="fa fa-eye "></i></Link> &nbsp;
       <Link to = "#"  > <i className="fa fa-edit"></i></Link> &nbsp;
       <Link to = "#"  > <i className="fa fa-trash"></i></Link> </div>  });
  })

const onSortFunction = {
  date(columnValue) {
    // Convert the string date format to UTC timestamp
    // So the table could sort it by number instead of by string
    return moment(columnValue, 'Do MMMM YYYY').valueOf();
  },
};

const customLabels = {
  first: '<<',
  last: '>>',
  prev: '<',
  next: '>',
  show: 'Display',
  entries: 'rows',
  noResults: 'There is no data to be displayed',
};

// In your render method
return(

<Datatable
  tableHeader={header}
  tableBody={body}
  keyName="userTable"
  tableClass="striped hover responsive"
  rowsPerPage={5}
  rowsPerPageOption={[5, 10, 15, 20]}
  initialSort={{prop: "username", isAscending: true}}
  onSort={onSortFunction}
  labels={customLabels}
/>
  )
 }
}

