import moment from 'moment'; // Example for onSort prop
import React, { Component } from 'react'; // Import React
import axios from 'axios';
import {Link, withRouter} from 'react-router-dom';
import Datatable from 'react-bs-datatable'; // Import this package
import swal from 'sweetalert';
import validator from 'validator';
import {  Modal,ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

class BusinessDataTable extends Component{ 
    constructor(props){
        super(props);
        this.state = {
        businessList : [],    
        modal : false,
        // businesscategory : {value : '', isValidate : true , message :''},
        businesscategory : ''
        }
      this.fetchBusiness = this.fetchBusiness.bind(this);
      this.toggle = this.toggle.bind(this);
      this.handleBusinessCategoryChange = this.handleBusinessCategoryChange.bind(this);
      };

      toggle(){
        this.setState({
          modal : !this.state.modal
        })
      }

      componentWillMount(){
        this.fetchBusiness();
      }

      // handleBusinessCategoryChange(event){
      //   const {name, value} = event.target;
      //   let state = this.state;
      //   state[name].message = '';
      //   state[name].value = value;
      //   this.setState(state);
      // }

      handleBusinessCategoryChange(event){
        this.setState({
          businesscategory : event.target.value
        })
      }

      validate(){
        let state = this.state;
        if(validator.isEmpty(state.businesscategory.value)){
          state.businesscategory.isValidate = false;
          state.businesscategory.message = 'Please Fill The Businee Category';
          this.setState(state);
          return false;
        }
        if (!state.productName.value.match(/^[A-Za-z ]+$/)) {
          state.productName.isValidate = false;
          state.productName.message = "Must Be Letters";
          this.setState(state);
          return false;
        }
        return true;
      }

        fetchBusiness(){
            axios.post('http://localhost:3300/api/fetchBusiness').then((response)=>{
              console.log('HHHHHHHHHHHHHHHHH',response);
              this.setState({
                businessList : response.data.doc
              })
            })
          }

          editBusinessCategory(e,i){
            console.log('WWWWWWWWWWWWWWWWWWWWWWWWWWWW',e.state.businessList[i]._id);
            let obj  = {};
            obj.businessId = e.state.businessList[i]._id
            obj.businesscategory = this.state['businesscategory'].value;
            axios.post('http://localhost:3300/api/editBusinessCategory',obj).then((response)=>{
              console.log('---------------------------',response.data);

              this.setState({
                businesscategory : response.data.businesscategory
              })
           })
       }


          deleteBusinessCat(e){
            swal({
              title: "Are you sure?",
              text: "Once deleted, you will not be able to recover this imaginary file!",
              icon: "warning",
              buttons: true,
              dangerMode: true,
            }).then((willDelete)=>{
              if(willDelete){
                axios.post('http://localhost:3300/api/deleteBusinessCategory',{
                  businessId: e,
                }).then(res=>{
                  swal("Poof! Your imaginary file has been deleted!",{
                    icon:"success",
                  });
                })
                setTimeout(function(){
                  return window.location = '/businesscategory';
                },2000)
              }else{
                swal("Your imaginary file is safe!");
              }
            });
           }


render(){
  // const state = this.state;
const header = [
  { title: 'Category', prop: 'category', sortable: true, filterable: true },
  { title: 'Status', prop: 'status', sortable: true ,filterable: true},
  // { title: 'Name Uppercased', prop: 'realnameuppercase', cell: (row) => row.realname.toUpperCase() },
  { title: 'Action', prop: 'action'    ,sortable: true , filterable: true },
  ];

let state = this.state;
const body = [];
  state.businessList.map((e,i)=>{
    body.push({
     'category':e.businesscategory,
     'status':      <td>
     <div className="card-options">
         <label className="custom-switch m-0">
           <input type="checkbox" value="1" className="custom-switch-input" />
           <span className="custom-switch-indicator"></span>
         </label>
       </div>
     </td>,
      'action': <div className="actiontrans" > 
       <Link to = "#" onClick = {this.toggle}   > <i onClick = {(e)=> this.editBusinessCategory(this,i)} value = {e._id} className="fa fa-edit"></i></Link>
       <Modal isOpen={this.state.modal} toggle={this.toggle} >
          <ModalHeader toggle={this.toggle}>Modal title</ModalHeader>
          <ModalBody>
                <div className="form-group">
                <label>Business Category  </label>
                <input type="text" className="form-control" name = "businesscategory" value = {this.state.businesscategory} onChange = {this.handleBusinessCategoryChange} />
                </div> 
           <div style={{ marginTop: 10 }}>

        </div>
        </ModalBody>
          <ModalFooter>

          </ModalFooter>
        </Modal>
       <Link to = "#" onClick = {this.deleteBusinessCat.bind(this,e._id)}  > <i className="fa fa-trash"></i></Link> </div>  });
  })

const onSortFunction = {
  date(columnValue) {
    // Convert the string date format to UTC timestamp
    // So the table could sort it by number instead of by string
    return moment(columnValue, 'Do MMMM YYYY').valueOf();
  },
};

const customLabels = {
  first: '<<',
  last: '>>',
  prev: '<',
  next: '>',
  show: 'Display',
  entries: 'rows',
  noResults: 'There is no data to be displayed',
};

// In your render method
return(

<Datatable
  tableHeader={header}
  tableBody={body}
  keyName="userTable"
  tableClass="striped hover responsive"
  rowsPerPage={5}
  rowsPerPageOption={[5, 10, 15, 20]}
  initialSort={{prop: "username", isAscending: true}}
  onSort={onSortFunction}
  labels={customLabels}
/>
  )
 }
}



export default BusinessDataTable;
