		this.setState(state);
	}

	validate(){
    let state = this.state;
    if(validator.isEmpty(state.businesscategoryId.value)){
      state.businesscategoryId.isValidate = false;
      state.businesscategoryId.message = "BusinessCategory Can Not Be Blank";
      this.setState(state);
      return false;
    }
    if(validator.isEmpty(state.categoryId.value)){
      state.categoryId.isValidate = false;
      state.categoryId.message = "BusinessCategory Can Not Be Blank";
      this.setState(state);
      return false;
    }
		if(validator.isEmpty(state.subcategory.value)){
			state.subcategory.isValidate = false;
			state.subcategory.message = 'Sub Category Can Not Be Blank';
			this.setState(state);
			return false;
    }
    if (!state.subcategory.value.match(/^[A-Za-z ]+$/)) {
      state.subcategory.isValidate = false;
      state.subcategory.message = "Must Be Letters";
      this.setState(state);
      return false;
    }
		return true;
