import moment from 'moment'; // Example for onSort prop
import React, { Component } from 'react'; // Import React
import axios from 'axios';
import {Link, withRouter} from 'react-router-dom';
import { render } from 'react-dom'; // Import render method
import Datatable from 'react-bs-datatable'; // Import this package
// import { connect } from 'react-redux';
import swal from 'sweetalert';

class BusinessDataTable extends Component{ 
    constructor(props){
        super(props);
        this.state = {
        businessList : [],            
        }
    this.fetchBusiness = this.fetchBusiness.bind(this);
      }



        componentWillMount(){
          this.fetchBusiness();
        }

        fetchBusiness(){
            axios.post('http://localhost:3300/api/fetchBusiness').then((response)=>{
              console.log('HHHHHHHHHHHHHHHHH',response);
              this.setState({
                businessList : response.data.doc
              })
            })
          }

render(){
const header = [
  { title: 'Vendor Name', prop: 'vendorname', sortable: true, filterable: true },
  { title: 'Store Name', prop: 'storename', sortable: true ,filterable: true},
  // { title: 'Name Uppercased', prop: 'realnameuppercase', cell: (row) => row.realname.toUpperCase() },
  { title: 'Email Id', prop: 'email',      sortable: true,filterable: true },
  { title: 'Mobile No', prop: 'mobile',sortable: true,filterable: true },
  { title: 'Action', prop: 'action'    ,sortable: true , filterable: true },
  ];

let state = this.state;
const body = [];
  state.businessList.map((e,i)=>{
    body.push({'vendorname' : e.name,
     'storename':e.storeName,
     'email': e.email,
      'mobile': e.mobile,
      'action': <div className="actiontrans" > 
       <Link to = "#"  > <i className="fa fa-eye "></i></Link> &nbsp;
       <Link to = "#"  > <i className="fa fa-edit"></i></Link> &nbsp;
       <Link to = "#"  > <i className="fa fa-trash"></i></Link> </div>  });
  })

const onSortFunction = {
  date(columnValue) {
    // Convert the string date format to UTC timestamp
    // So the table could sort it by number instead of by string
    return moment(columnValue, 'Do MMMM YYYY').valueOf();
  },
};

const customLabels = {
  first: '<<',
  last: '>>',
  prev: '<',
  next: '>',
  show: 'Display',
  entries: 'rows',
  noResults: 'There is no data to be displayed',
};

// In your render method
return(

<Datatable
  tableHeader={header}
  tableBody={body}
  keyName="userTable"
  tableClass="striped hover responsive"
  rowsPerPage={5}
  rowsPerPageOption={[5, 10, 15, 20]}
  initialSort={{prop: "username", isAscending: true}}
  onSort={onSortFunction}
  labels={customLabels}
/>
  )
 }
}

