import React from 'react';
import ReactDOM from 'react-dom';
import validator from 'validator';
import './createsubcategorypage.css';
import axios from 'axios';
class Createsubcategorypage extends React.Component{
  constructor(props){
		super(props);
		this.state = {
      category :  {value :'', isValidate:true, message:''},
      businessList : [],
		}
    this.handleChange = this.handleChange.bind(this);
    this.fetchBusiness = this.fetchBusiness.bind(this);
		this.submit = this.submit.bind(this);
	}

  componentWillMount(){
    this.fetchBusiness();
  }

  fetchBusiness(){
    axios.post('http://localhost:3300/api/fetchBusiness').then((response)=>{
      console.log('RRRRRRRRRRRRRRRRR',response);
      this.setState({
        businessList : response.data.doc
      })
    })
  }

	handleChange(event){
		const {name,value} = event.target;
		let state = this.state;
		state[name].message = '';
		state[name].value = value;
		this.setState(state);
	}

	validate(){
		let state = this.state;
		if(validator.isEmpty(state.category.value)){
			state.category.isValidate = false;
			state.category.message = 'Category Can Not Be Blank';
			this.setState(state);
			return false;
    }
    if (!state.category.value.match(/^[A-Za-z ]+$/)) {
      state.category.isValidate = false;
      state.category.message = "Must Be Letters";
      this.setState(state);
      return false;
    }
		return true;
	}

	submit(event){
		event.preventDefault();
		let isValid = this.validate();
		if(isValid){
			let obj = {};
			obj.category = this.state['category'].value;
			console.log('RRRRRRRRRRRR',obj);
		}
	}
	render()	{
    const state = this.state;
		return(
        <div className="my-3 my-md-5">
          <div className="container">
            <div className="page-header">
              <h4 className="page-title">Create Sub Category</h4>
              <ol className="breadcrumb">
                <li className="breadcrumb-item"><a href="#">Home</a></li>
                <li className="breadcrumb-item active" aria-current="page">Create Sub Category</li>
              </ol>
            </div>
            <div className="row">
              <div className="col-md-5 col-lg-5">
                <form  className="card" onSubmit = {this.submit}>
                  <div className="card-header">
                    <h3 className="card-title">Hi ! Admin Create Your New Sub Category</h3>
                  </div>
                  <div className="card-body">
                    <div className="row">
                      <div className="col-md-12 col-lg-12">
                        <div className="form-group">

                         <label className="form-label">Select Category</label>

                          <select name="Category"  className="form-control custom-select">  
                          {
                            this.state.businessList.map((e,i)=>{
                              return(
                              <React.Fragment key = {i}>
                                <option>Select</option>
                            <option >{e.businesscategory}</option>
                            </React.Fragment>
                              )
                            })
                          }
                          </select>                      
                        </div>
                    </div>
                      <div className="col-md-12 col-lg-12">
                        <div className="form-group">
                          <label className="form-label">Sub Category Name </label>
                          <input type="text" className="form-control" name="category" value = {state.category.value} onChange = {this.handleChange} />
                          <div style = {{fontSize: 13, color: 'red'}}>
                            {state.category.message}
                          </div>
                        </div>
                      </div>
                      <div className="col-md-12 col-lg-12">
                        <button type="submit" className="btn btn-primary btn-block createcategory">Create</button>
                      </div>
                    </div>
                  </div>
                </form>
              </div>
              <div className="col-md-7 col-lg-7">
              <div className="card">
                <div className="card-body">
                                  <div className="table-responsive">
                  <table id="example" className="table table-striped table-bordered">
                    <thead>
                      <tr>
                        <th className="wd-15p">Sr. No</th>
                        <th className="wd-15p">Category</th>
                        <th className="wd-15p">Sub Category</th>
                        <th className="wd-20p">Status</th>
                        <th className="wd-25p">Action</th>
                      </tr>
                    </thead>
                    <tbody>
                      <tr>
                        <td>1</td>
                        <td>Men Fashion</td>
                        <td>Jeans</td>
                         <td>
                        <div className="card-options">
                            <label className="custom-switch m-0">
                              <input type="checkbox" value="1" className="custom-switch-input" />
                              <span className="custom-switch-indicator"></span>
                            </label>
                          </div>
                        </td>
                        <td>
                          <div className="actiontrans">
                            <a href="#">Delete</a>
                          </div>
                        </td>
                      </tr>
                      <tr>
                        <td>2</td>
                        <td>Women Fashion</td>
                        <td>T-Shirt</td>
                         <td>
                        <div className="card-options">
                            <label className="custom-switch m-0">
                              <input type="checkbox" value="1" className="custom-switch-input" />
                              <span className="custom-switch-indicator"></span>
                            </label>
                          </div>
                        </td>
                        <td>
                          <div className="actiontrans">
                            <a href="#">Delete</a>
                          </div>
                        </td>
                      </tr>
                      <tr>
                        <td>3</td>
                        <td>Electronics</td>
                        <td>TV</td>
                         <td>
                        <div className="card-options">
                            <label className="custom-switch m-0">
                              <input type="checkbox" value="1" className="custom-switch-input" />
                              <span className="custom-switch-indicator"></span>
                            </label>
                          </div>
                        </td>
                        <td>
                          <div className="actiontrans">
                            <a href="#">Delete</a>
                          </div>
                        </td>
                      </tr>
                    </tbody>
                  </table>
                </div>
                                </div>
              </div>
              </div>
            </div>
          </div>
        </div>


			)
	}
}

export default Createsubcategorypage;
