import moment from 'moment'; // Example for onSort prop
import React, { Component } from 'react'; // Import React
import axios from 'axios';
import {Link, withRouter} from 'react-router-dom';
import { render } from 'react-dom'; // Import render method
import Datatable from 'react-bs-datatable'; // Import this package
// import { connect } from 'react-redux';
import swal from 'sweetalert';
import {  Modal,ModalHeader, ModalBody, ModalFooter } from 'reactstrap';


class CategoryDataTable extends Component{ 
    constructor(props){
        super(props);
        this.state = {
        // businessList : [],  
        categoryList : [], 
        modal : false,        
        }
    this.fetchCategory = this.fetchCategory.bind(this);
      }
        componentWillMount(){
          this.fetchCategory();
        }

        toggle(){
          this.setState({
            modal : !this.state.modal
          })
        }

        fetchCategory(){
            axios.post('http://localhost:3300/api/fetchcategory').then((resp)=>{
              console.log('OOOOOOOOOOOOOOOO',resp.data.category);
              this.setState({
                categoryList : resp.data.category,
              })
            })
          }

          deleteCategory(e){
            swal({
              title: "Are you sure?",
              text: "Once deleted, you will not be able to recover this imaginary file!",
              icon: "warning",
              buttons: true,
              dangerMode: true,
            }).then((willDelete)=>{
              if(willDelete){
                axios.post('http://localhost:3300/api/deleteCategory',{
                  businessId: e,
                }).then(res=>{
                  swal("Poof! Your imaginary file has been deleted!",{
                    icon:"success",
                  });
                })
                setTimeout(function(){
                  return window.location = '/category';
                },2000)
              }else{
                swal("Your imaginary file is safe!");
              }
            });
           }


render(){
const header = [
  { title: 'Business Category', prop: 'businesscategory', sortable: true, filterable: true },
  { title: 'Category', prop: 'category', sortable: true, filterable: true },
  { title: 'Status', prop: 'status', sortable: true ,filterable: true},
  { title: 'Action', prop: 'action'    ,sortable: true , filterable: true },
  ];

let state = this.state;
const body = [];
  state.categoryList.map((e,i)=>{
      console.log('FFFFFFFFFF',e._id);
      body.push({
     'businesscategory':e.businessId.businesscategory,
     'category' : e.category,
     'status':      
     <div className="card-options">
         <label className="custom-switch m-0">
           <input type="checkbox" value="1" className="custom-switch-input" />
           <span className="custom-switch-indicator"></span>
         </label>
       </div>
     ,
      'action': <div className="actiontrans" > 
      <Link to = "#" onClick = {this.toggle} > <i className="fa fa-edit"></i></Link>
      <Modal isOpen={this.state.modal} toggle={this.toggle} >
          <ModalHeader toggle={this.toggle}>Modal title</ModalHeader>
          <ModalBody>
                <div className="form-group">
                <label>Business Category  </label>
                <input type="text" className="form-control" />
                </div>     
                <div className="form-group">

                  </div>

           <div style={{ marginTop: 10 }}>

        </div>
        </ModalBody>
          <ModalFooter>

          </ModalFooter>
        </Modal>
       <Link to = "#"  onClick = {this.deleteCategory.bind(this,e._id)}  > <i className="fa fa-trash"></i></Link> </div>  });
})
 console.log('Hello',body);
const onSortFunction = {
  date(columnValue) {
    // Convert the string date format to UTC timestamp
    // So the table could sort it by number instead of by string
    return moment(columnValue, 'Do MMMM YYYY').valueOf();
  },
};

const customLabels = {
  first: '<<',
  last: '>>',
  prev: '<',
  next: '>',
  show: 'Display',
  entries: 'rows',
  noResults: 'There is no data to be displayed',
};

// In your render method
return(

<Datatable
  tableHeader={header}
  tableBody={body}
  keyName="userTable"
  tableClass="striped hover responsive"
  rowsPerPage={5}
  rowsPerPageOption={[5, 10, 15, 20]}
  initialSort={{prop: "username", isAscending: true}}
  onSort={onSortFunction}
  labels={customLabels}
/>
  )
 }
}



export default CategoryDataTable;
