import React from 'react';
import ReactDOM from 'react-dom';
import validator from 'validator';
import './createcategorypage.css';
class Createcategorypage extends React.Component{

	constructor(props){
		super(props);
		this.state = {
			businesscategory :  {value :'', isValidate:true, message:''},
		}
		this.handleChange = this.handleChange.bind(this);
		this.submit = this.submit.bind(this);
	}

	handleChange(event){
		const {name,value} = event.target;
		let state = this.state;
		state[name].message = '';
		state[name].value = value;
		this.setState(state);
	}

	validate(){
		let state = this.state;
		if(validator.isEmpty(state.businesscategory.value)){
			state.businesscategory.isValidate = false;
			state.businesscategory.message = 'Category Can Not Be Blank';
			this.setState(state);
			return false;
    }
    if (!state.businesscategory.value.match(/^[A-Za-z ]+$/)) {
      state.businesscategory.isValidate = false;
      state.businesscategory.message = "Must Be Letters";
      this.setState(state);
      return false;
    }
		return true;
	}

	submit(event){
		event.preventDefault();
		let isValid = this.validate();
		if(isValid){
			let obj = {};
			obj.businesscategory = this.state['businesscategory'].value;
			console.log('RRRRRRRRRRRR',obj);
		}
	}

	render(){
    console.log('????????????????????/',this.state.businesscategory);
    const state = this.state;
		return(
        <div className="my-3 my-md-5">
          <div className="container">
            <div className="page-header">
              <h4 className="page-title">Create Category</h4>
              <ol className="breadcrumb">
                <li className="breadcrumb-item"><a href="#">Home</a></li>
                <li className="breadcrumb-item active" aria-current="page">Create Category</li>
              </ol>
            </div>
            <div className="row">
              <div className="col-md-5 col-lg-5">
                <form  className="card" onSubmit = {this.submit} >
                  <div className="card-header">
                    <h3 className="card-title">Hi ! Admin Create Your New Category</h3>
                  </div>
                  <div className="card-body">
                    <div className="row">
                      <div className="col-md-12 col-lg-12">
                        <div className="form-group">
                          <label className="form-label">Category Name </label>
                          <input type="text" className="form-control" name="businesscategory" value = {state.businesscategory.value} onChange = {this.handleChange} />
                          <div style = {{fontSize: 13, color: 'red'}}>
                            {state.businesscategory.message}
                          </div>
                        </div>
                      </div>
                      <div className="col-md-12 col-lg-12">
                        <button type="submit" className="btn btn-primary btn-block createcategory">Create</button>
                      </div>
                    </div>
                  </div>
                </form>
              </div>
              <div className="col-md-7 col-lg-7">
              <div className="card">
                <div className="card-body">
                                  <div className="table-responsive">
                  <table id="example" className="table table-striped table-bordered">
                    <thead>
                      <tr>
                        <th className="wd-15p">Sr. No</th>
                        <th className="wd-15p">Category</th>
                        <th className="wd-20p">Status</th>
                        <th className="wd-25p">Action</th>
                      </tr>
                    </thead>
                    <tbody>
                      <tr>
                        <td>1</td>
                        <td>Men Fashion</td>
                         <td>
                        <div className="card-options">
                            <label className="custom-switch m-0">
                              <input type="checkbox" value="1" className="custom-switch-input" />
                              <span className="custom-switch-indicator"></span>
                            </label>
                          </div>
                        </td>
                        <td>
                          <div className="actiontrans">
                            <a href="#">Delete</a>
                          </div>
                        </td>
                      </tr>
                      <tr>
                        <td>2</td>
                        <td>Women Fashion</td>
                         <td>
                        <div className="card-options">
                            <label className="custom-switch m-0">
                              <input type="checkbox" value="1" className="custom-switch-input" />
                              <span className="custom-switch-indicator"></span>
                            </label>
                          </div>
                        </td>
                        <td>
                          <div className="actiontrans">
                            <a href="#">Delete</a>
                          </div>
                        </td>
                      </tr>
                      <tr>
                        <td>3</td>
                        <td>Electronics</td>
                         <td>
                        <div className="card-options">
                            <label className="custom-switch m-0">
                              <input type="checkbox" value="1" className="custom-switch-input" />
