import moment from 'moment'; // Example for onSort prop
import React, { Component } from 'react'; // Import React
import axios from 'axios';
import {Link, withRouter} from 'react-router-dom';
import { render } from 'react-dom'; // Import render method
import Datatable from 'react-bs-datatable'; // Import this package
// import { connect } from 'react-redux';
import swal from 'sweetalert';

class BusinessDataTable extends Component{ 
    constructor(props){
        super(props);
        this.state = {
        businessList : [],            
        }
    this.fetchBusiness = this.fetchBusiness.bind(this);
      }



        componentWillMount(){
          this.fetchBusiness();
        }

        fetchBusiness(){
            axios.post('http://localhost:3300/api/fetchBusiness').then((response)=>{
              console.log('HHHHHHHHHHHHHHHHH',response);
              this.setState({
                businessList : response.data.doc
              })
            })
          }

          deleteBusinessCat(e){
            swal({
              title: "Are you sure?",
              text: "Once deleted, you will not be able to recover this imaginary file!",
              icon: "warning",
              buttons: true,
              dangerMode: true,
            }).then((willDelete)=>{
              if(willDelete){
                axios.post('http://localhost:3300/api/deleteBusinessCategory',{
                  businessId: e,
                }).then(res=>{
                  swal("Poof! Your imaginary file has been deleted!",{
                    icon:"success",
                  });
                })
                setTimeout(function(){
                  return window.location = '/category';
                },2000)
              }else{
                swal("Your imaginary file is safe!");
              }
            });
           }


render(){
const header = [
  { title: 'Business Category', prop: 'businesscategory', sortable: true, filterable: true },
  { title: 'Category', prop: 'category', sortable: true, filterable: true },
  { title: 'Status', prop: 'status', sortable: true ,filterable: true},
  // { title: 'Name Uppercased', prop: 'realnameuppercase', cell: (row) => row.realname.toUpperCase() },
  { title: 'Action', prop: 'action'    ,sortable: true , filterable: true },
  ];

let state = this.state;
const body = [];
  state.businessList.map((e,i)=>{
    body.push({
     'businesscategory':e.businesscategory,
    //  'category' : 
     'status':      <td>
     <div className="card-options">
         <label className="custom-switch m-0">
           <input type="checkbox" value="1" className="custom-switch-input" />
           <span className="custom-switch-indicator"></span>
         </label>
       </div>
     </td>,
      'action': <div className="actiontrans" > 
       <Link to = "#" onClick = {this.deleteBusinessCat.bind(this,e._id)}  > <i className="fa fa-trash"></i></Link> </div>  });
  })

const onSortFunction = {
  date(columnValue) {
    // Convert the string date format to UTC timestamp
    // So the table could sort it by number instead of by string
    return moment(columnValue, 'Do MMMM YYYY').valueOf();
  },
};

const customLabels = {
  first: '<<',
  last: '>>',
  prev: '<',
  next: '>',
  show: 'Display',
  entries: 'rows',
  noResults: 'There is no data to be displayed',
};

// In your render method
return(

<Datatable
  tableHeader={header}
  tableBody={body}
  keyName="userTable"
  tableClass="striped hover responsive"
  rowsPerPage={5}
  rowsPerPageOption={[5, 10, 15, 20]}
  initialSort={{prop: "username", isAscending: true}}
  onSort={onSortFunction}
  labels={customLabels}
/>
  )
 }
}

