  { title: 'Action', prop: 'action'    ,sortable: true , filterable: true },
  ];

let state = this.state;
const body = [];
  state.productList.map((e,i)=>{
    body.push({'category' : e.category,
     'productname':e.productName,
     'price': e.productPrice,
      'quantity': e.quantity,
