import React from 'react';
import { Link } from 'react-router-dom'
import axios from 'axios';
import './categories.css';
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import Slid from '../Slid';
import ItemsCarousel from 'react-items-carousel'
const URL = process.env.REACT_APP_LOCAL;


class Categories extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      productList : [],
      visible  : false,


    }
    this.allProducts = this.allProducts.bind(this);
  }

  allProducts(){
    this.setState({visible : true});
    axios.post(URL+'/api/user/fetchProduct').then((response)=>{
      console.log('OOOOOOOOOOOOOOOOOOO',response.data.product);
      this.setState({visible : false});
      this.setState({
        productList : response.data.product,
      })
    })
  }

  componentDidMount(){
    this.allProducts();
  }

 	render(){
    console.log('44444444444444444444444444',this.state.productList)
    const responsive = {
      desktop: {
        breakpoint: { max: 3000, min: 1024 },
        items: 5,
        slidesToSlide: 1, // optional, default to 1.
      },
      tablet: {
        breakpoint: { max: 1024, min: 464 },
        items: 2,
        slidesToSlide: 2, // optional, default to 1.
      },
      mobile: {
        breakpoint: { max: 464, min: 0 },
        items: 1,
        slidesToSlide: 1, // optional, default to 1.
      },
    };
		return(
<div style={{"padding":"0 60px","maxWidth":1000,"margin":"0 auto"}}>
  <ItemsCarousel
    placeholderItem={<div style={{ height: 200, background: '#EEE' }} />}
    enablePlaceholder={true}
    numberOfPlaceholderItems={3}
    numberOfCars={3}
    gutter={12}
    slidesToScroll={2}
    chevronWidth={60}
    outsideChevron={true}
    showSlither={false}
    firstAndLastGutter={false}
    activeItemIndex={this.state.activeItemIndex}
    requestToChangeActive={value => this.setState({ activeItemIndex: value })}
    rightChevron={'>'}
    leftChevron={'<'}
    outsideChevron
  >
    {this.state.productList.map((e, i) =>
      <div
        key={i}
        style={{
          height: 200,
          background: 'url(https://placeimg.com/380/200/nature)'
        }}
      />
    )}
  </ItemsCarousel>
</div>

			)
	}
}

export default Categories;
