import React from 'react';
import { Link } from 'react-router-dom'
import axios from 'axios';
import '../src/home/categories.css';
import ItemsCarousel from 'react-items-carousel';
import range from 'lodash/range';

class Slid extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      productList : [],
      children: [],
      activeItemIndex: 0,
    }
    this.allProducts = this.allProducts.bind(this);
  }

  allProducts(){
    axios.post('http://localhost:3100/api/fetchProduct').then((resp)=>{
      console.log('OOOOOOOOOOOOOOOOOOO',resp);
      this.setState({
        productList : resp.data.doc,
      })
    })
  }

  componentDidMount(){
    this.allProducts();
  }

  // productList = n => range(n).map(i => <div key={i} style={{ height: 200 }}>{i}</div>);

  changeActiveItem = (activeItemIndex) => this.setState({ activeItemIndex });

	render(){
    const {
      activeItemIndex,
      children,
    } = this.state
   console.log('!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!',this.state.productList);
		return(
<div> 
   <section className="main-container col1-layout home-content-container categories-fluid">
    <div className="container">
      <div className="std">
        <div className="best-seller-pro wow bounceInUp animated">
          <div className="slider-items-products">
            <div className="new_title center">
              <h2>Categories</h2>
            </div>
            {

            }
       <ItemsCarousel
        // Placeholder configurations
        enablePlaceholder
        numberOfPlaceholderItems={3}
        minimumPlaceholderTime={1000}
        placeholderItem={      
          this.state.productList.map((e,i)=>{
            return(
              <React.Fragment key = {i}>
                 <div style = {{width: '240px'}} className="item">
              <div className="col-item">

                <div className="product-image-area"> <a className="product-image" title="Sample Product" href="#"> <img src = {e.file} className="img-responsive" alt="product-image" /> </a>
                </div>
                <div className="info">
                  <div className="info-inner">
                    <div className="item-title"> <Link title=" Sample Product" to="/Productlist"> </Link> {e.category} </div>
                  </div>

                  <div className="clearfix"> </div>
                </div>
              </div>
            </div>  
              </React.Fragment>
            )
          })

        } 
        // Carousel configurations
        numberOfCards={4}
        gutter={12}
        showSlither={true}
        firstAndLastGutter={true}
        freeScrolling={false}
        // Active item configurations
        requestToChangeActive={this.changeActiveItem}
        activeItemIndex={activeItemIndex}
        activePosition={'center'}
        chevronWidth={24}
        rightChevron={'>'}
        leftChevron={'<'}
        outsideChevron={false}>
        {children}
      </ItemsCarousel>               
          </div>
        </div>
      </div>
    </div>
  </section>
  </div>
			)
	}
}

export default Slid;
