import React from 'react';
import ReactDOM from 'react-dom';
import { Link, withRouter } from 'react-router-dom'
import './addressdetail.css';
import validator from 'validator';

import {connect} from 'react-redux';
import swal from 'sweetalert';
import axios from 'axios';
const URL = process.env.REACT_APP_LOCAL;

var divStyle = {
  cursor:'pointer',
};

class Addressdetail extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            myAddress : [],
            isChecked:0,
            fullName :{value : '', isValidate : true , message : '' },
            mobile :{value : '', isValidate : true , message : '' },
            pincode :{value : '', isValidate : true , message : '' },
            address :{value : '', isValidate : true , message : '' },
            city :{value : '', isValidate : true , message : '' },
            state :{value : '', isValidate : true , message : '' },
            landmark :{value : '', isValidate : true , message : '' },
            alterNumber :{value : '', isValidate : true , message : '' },
        }
        this.handleChangeFirstName = this.handleChangeFirstName.bind(this);
        this.saveAddress = this.saveAddress.bind(this);
    }

    handleChangeFirstName(event){
      console.log('eventeventevent',event)
      const {name,value} = event.target;
      let state = this.state;
      state[name].message = '';
      state[name].value = value;
      this.setState(state);
    }

    componentWillMount(){
      this.fetchMyAddress();
    }
    fetchMyAddress(){
      // console.log('this.props.userId',this.props.userId)
      if(this.props.userId){
        axios.post(URL+'/api/user/getAddress',{
          userId:this.props.userId
        }).then((response)=>{
          console.log('this.responsefdfddfdddddddddd',response.data.getAddress);
          this.setState({
            myAddress : response.data.getAddress,
          })
        })
      }else{
        swal({
          title: "OOPS",
          text: "Session expired.Please Login!",
          icon: "warning",
          dangerMode: true,
          closeOnClickOutside: false,
        }).then((d)=>{
           //console.log('ddddddddddddddddddd',d)
            if(d){
            return window.location = "/Login"
          }
         })
      }

    }

    removeAddress = (addressId) => {
      axios.post(URL+'/api/user/deleteAddress',{
        addressId:addressId
      }).then((response)=>{
        if(response.data.code==100){
          return window.location.reload()
        }else{
          swal({
            title: "OOPS",
            text: "Some error found.",
            icon: "warning",
            dangerMode: true,
            closeOnClickOutside: false,
          }).then((d)=>{
             //console.log('ddddddddddddddddddd',d)
              if(d){
              //return window.location = "/Login"
            }
           })
        }
      })
    }


    validate (){
      let state = this.state;
      if (validator.isEmpty(state.fullName.value)){
        console.log('eventeventeventevent')
        state.fullName.isValidate = false;
        state.fullName.message = 'Full Name Is Required';
        this.setState(state);
        return false;
      }

      if (validator.isEmpty(state.lastName.value)){
        state.lastName.isValidate = false;
        state.lastName.message = 'Please Fill The Last Name';
        this.setState(state);
        return false;
      }

      if(validator.isEmpty(state.email.value)){
        state.email.isValidate = false;
        state.email.message = 'Please Fill The E-mail';
        this.setState(state);
        return false;
      }

      if(!validator.isEmail(state.email.value)){
        state.email.isValidate = false;
        state.email.message = 'Invalid E-mail';
        this.setState(state);
        return false;
      }
      if(validator.isEmpty(state.mobile.value)){
        state.mobile.isValidate = false;
        state.mobile.message = 'Please Fill The Mobile';
        this.setState(state);
        return false;
      }
      if(!validator.isNumeric(state.mobile.value)){
        state.mobile.isValidate = false;
        state.mobile.message = 'Must be Number';
        this.setState(state);
        return false;
      }

      if(validator.isEmpty(state.password.value)){
        state.password.isValidate = false;
        state.password.message = 'Please Fill The Password';
        this.setState(state);
        return false;
      }

      if (validator.isEmpty(state.cpassword.value)){
        state.cpassword.isValidate = false;
        state.cpassword.message = 'Please Fill The Confirm Password';
        this.setState(state);
        return false;
      }

      if(!validator.equals(state.password.value, state.cpassword.value)){
        state.cpassword.value = false;
        state.cpassword.message = "Password and ConfirmPassword Not Match";
        this.setState(state);
        return false;
      }
         return  true;
    }


    saveAddress(event){
      event.preventDefault();
      let isValid = this.validate();
      if(isValid){
        let obj = {}
        obj.type = this.state.type;
        obj.firstName = this.state['firstName'].value;
        obj.lastName =  this.state['lastName'].value;
        obj.email = this.state['email'].value;
        obj.mobile = this.state['mobile'].value;
        obj.password = this.state['password'].value;
        obj.cpassword = this.state['cpassword'].value; 

        console.log('?????????????????????/',obj);

        axios.post(URL+'/api/user/Signup',obj).then((response)=>{
          console.log('77777777777777777',response);
          if(response.data.status === true){
            swal({
              title: "Success!",
              text: response.data.message,
              icon: "success",
              dangerMode: false,
              closeOnClickOutside: false,
            }).then((d)=>{
             if(d){
               window.location = '/Login';
             }
            })

          }else {
            swal ("Error",
            `${response.data.message}`,
            "error",
            ).then((d)=>{
            })
          }
        })
      }
    }

	render()
	{
    const state = this.state;
		return(
        <section className="col-main col-sm-8  wow bounceInUp animated addressdeail-fluid">
          <div className="category-title">
               <h1>Delivery Address</h1>
            </div>
            <div className="showaddress">
              <div className="row">

                {
                  this.state.myAddress.map((e,i)=>{
                    this.state.isChecked=this.state.isChecked+1;
                    return(
                      <div className="col-sm-6">
                        <h4>Saved Address {this.state.isChecked}</h4>
                        <div className="addresss">
                        <h2>{e.fullName}</h2>
                        <p>{e.address}</p>
                        <p>{e.city} , {e.state} ,{e.pincode}</p>
                        <p>Phone: <span>+{e.mobile}</span></p>
                        <div className="deleteaddress" style={divStyle}  onClick={() => this.removeAddress(e._id)}><i className="fa fa-close"></i></div>
                        {
                          this.state.isChecked==1 ?
                            <div className="rightsign"><i className="fa fa-check"></i></div>
                            :
                            <div className="rightsign"></div>
                        }

                        </div>
                      </div>
                    )

                    })

                    }


              </div>
