    this.state = {
      specificationList : [],
    }
    this.fetchSpecification = this.fetchSpecification.bind(this)
  }

  componentWillMount(){
    this.fetchSpecification();
  }

  fetchSpecification(){
    let obj = {};
    obj.subCategoryId = "5d7215b1c86f411656184ccd"
