        <div className="modal-dialog" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <button type="button" className="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">&times;</span></button>
            </div>
            <div className="modal-body">
              <div className="row">
                <div className="col-sm-6">
                    <div className="loginsignup one">
                      <ul className="nav nav-tabs" role="tablist">
                        <li role="presentation" className="active"><a href="#Signup" aria-controls="Signup" role="tab" data-toggle="tab">Signup</a></li>
                        <li role="presentation"><a href="#Login" aria-controls="Login" role="tab" data-toggle="tab">Login</a></li>
                      </ul>
                      <div className="tab-content">
                        <div role="tabpanel" className="tab-pane active" id="Signup">
                          <div className="signupform">
                            <div className="heafingpoint">
                               <h3>New user Create Account!</h3>
                               <div className="formdesign">
                                      <form className="form-inline"
                                       onSubmit = {this.registerUser} 
                                       >
                                      <div className="row">
                                         <div className="col-sm-6">
                                            <div className="form-group">
                                              <input type="text" className="form-control" name = "firstName" 
                                              value = {state.firstName.value} onChange = {this.handleChangeFirstName} 
                                              placeholder="First Name"/>
                                              <label >{state.firstName.message} </label>
                                            </div>
                                         </div>
                                         <div className="col-sm-6">
                                            <div className="form-group">
                                              <input type="text" className="form-control" name = "lastName"
                                               value = {state.lastName.value} onChange = {this.handleChangeLastName}
                                                placeholder="Last Name"/>
                                              <div style={{ fontSize: 13, color: "red" }}>
                                                 {state.lastName.message}
                                              </div>
                                            </div>
                                         </div>
                                         <div className="col-sm-12">
                                            <div className="form-group">
                                              <input type="email" className="form-control" name = "email"
                                               value = {state.email.value} onChange = {this.handleChangeEmail}
                                                placeholder="Email Address"/>
                                              <div style={{ fontSize: 13, color: "red" }}>
                                                 {state.email.message}
                                              </div>
                                            </div>
                                         </div>
                                         <div className="col-sm-12">
                                            <div className="form-group">
                                              <input type="password" className="form-control" name = "password"
                                               value = {state.password.value} onChange = {this.handleChangePassword} 
                                               placeholder="Password"/>
                                              <div style={{ fontSize: 13, color: "red" }}>
                                                 {state.password.message}
                                              </div>
                                            </div>
                                         </div>
                                         <div className="col-sm-12">
                                            <div className="form-group">
                                              <input type="password" className="form-control" name = "cpassword" 
                                              value = {state.cpassword.value} onChange = {this.handleChangeCPassword} 
                                              placeholder="Confirm Password"/>
                                              <div style={{ fontSize: 13, color: "red" }}>
                                                 {state.cpassword.message}
                                              </div>
                                            </div>
                                         </div>
                                         <div className="col-sm-12">
                                            <div className="form-group">
                                               <button type="submit" className="btn btn-default btn-two">Submit</button>
                                            </div>
                                         </div>
                                      </div>
                                      </form>
                               </div>
                            </div>
                          </div>
                        </div>
                        <div role="tabpanel" className="tab-pane" id="Login">
                          <div className="signupform">
                            <div className="heafingpoint">
                               <h3>Welcome Back!</h3>
                               <div className="formdesign">
                                      <form className="form-inline" onSubmit = {this.loginUser}>
                                      <div className="row">
                                         <div className="col-sm-12">
                                            <div className="form-group">
                                              <input type="email" name = "mail" value = {state.mail.value} onChange = {this.handleChangeMail} className="form-control" placeholder="Email Address"/>
                                              <div style={{ fontSize: 13, color: "red" }}>
                                                 {state.mail.message}
                                              </div>
                                            </div>
                                         </div>
                                         <div className="col-sm-12">
                                            <div className="form-group">
                                              <input type="passages" className="form-control" name = "pass" value = {state.pass.value} onChange = {this.handleChangePass} placeholder="Password"/>
                                              <div style={{ fontSize: 13, color: "red" }}>
                                                 {state.pass.message}
                                              </div>
                                            </div>
                                         </div>
                                         <div className="col-sm-12">
                                            <div className="form-group">
                                               <button type="submit" className="btn btn-default">Login</button>
                                            </div>
                                         </div>
                                      </div>
                                      </form>
                                      <div class="forgompass">
                                          <a href="#">Forgot Password?</a>
                                      </div>
                               </div>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>

                    <div className = "Verifyuser two">
                    <div role="tabpanel" className="tab-pane active" id="verifyUser" >
                          <div className="signupform">
                            <div className="heafingpoint">
                               <h3>Please Verify Your Account!</h3>
                               <div className="formdesign">
                                      <form className="form-inline"
                                       onSubmit = {this.registerUser} 
